/**
 * Generated by `createschema property.PropertyResident 'organization:Relationship:Organization:PROTECT; property:Relationship:Property:PROTECT; contact?:Relationship:Contact:SET_NULL; unitName:Text; name:Text; email:Text; phone:Text;'`
 */
const { checkOrganizationPermission } = require('@condo/domains/organization/utils/accessSchema')
const get = require('lodash/get')
const { PropertyResident } = require('../utils/serverSchema')

async function canReadPropertyResidents ({ authentication: { item: user } }) {
    if (!user) return false
    if (user.isAdmin) return {}
    return {
        organization: { employees_some: { user: { id: user.id }, isBlocked: false } },
    }
}

async function canManagePropertyResidents ({ authentication: { item: user }, originalInput, operation, itemId, context }) {
    if (!user) return false
    if (user.isAdmin) return true
    if (operation === 'create') {
        const organizationId = get(originalInput, ['organization', 'connect', 'id'])
        const canManagePropertyResidents = await checkOrganizationPermission(user.id, organizationId, 'canManagePropertyResidents')
        return canManagePropertyResidents
    } else if (operation === 'update') {
        const [resident] = await PropertyResident.getAll(context, { id: itemId })
        if (!resident) {
            return false
        }
        const canManagePropertyResidents = await checkOrganizationPermission(user.id, resident.organization.id, 'canManagePropertyResidents')
        return canManagePropertyResidents
    }
    return false
}

/*
  Rules are logical functions that used for list access, and may return a boolean (meaning
  all or no items are available) or a set of filters that limit the available items.
*/
module.exports = {
    canReadPropertyResidents,
    canManagePropertyResidents,
}
