/**
 * Generated by `createschema property.Resident 'organization:Relationship:Organization:PROTECT; property:Relationship:Property:PROTECT; contact:Relationship:Contact:SET_NULL; billingAccount?:Relationship:BillingAccount:SET_NULL; unitName:Text; name:Text; email:Text; phone:Text;'`
 */

const faker = require('faker')
const { createTestBillingAccount } = require('@condo/domains/billing/utils/testSchema')
const { createTestBillingProperty } = require('@condo/domains/billing/utils/testSchema')
const { makeContextWithOrganizationAndIntegrationAsAdmin } = require('@condo/domains/billing/utils/testSchema')
const { createTestContact } = require('@condo/domains/contact/utils/testSchema')
const { createTestPhone } = require('@condo/domains/user/utils/testSchema')
const { createTestOrganizationEmployee } = require('@condo/domains/organization/utils/testSchema')
const { createTestOrganizationEmployeeRole } = require('@condo/domains/organization/utils/testSchema')
const { createTestOrganization } = require('@condo/domains/organization/utils/testSchema')
const { makeLoggedInAdminClient, makeClient, UUID_RE, DATETIME_RE } = require('@core/keystone/test.utils')
const { makeClientWithProperty } = require('@condo/domains/property/utils/testSchema')

const { Resident, createTestResident, updateTestResident } = require('@condo/domains/property/utils/testSchema')
const { catchErrorFrom, expectToThrowAccessDeniedErrorToObj, expectToThrowAccessDeniedErrorToObjects } = require('../../common/utils/testSchema')

describe('Resident', () => {

    describe('validations', async () => {
        it('throws error on create record with same set of fields: "property", "unitName", "phone"', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()
            const duplicatedFields = {
                property: { connect: { id: userClient.property.id } },
                unitName: faker.random.alphaNumeric(3),
                phone: createTestPhone(),
            }
            await createTestResident(adminClient, userClient.organization, userClient.property, duplicatedFields)

            await catchErrorFrom(async () => {
                await createTestResident(adminClient, userClient.organization, userClient.property, duplicatedFields)
            }, ({ errors, data }) => {
                expect(errors[0].message).toMatch('You attempted to perform an invalid mutation')
                expect(errors[0].data.messages[0]).toMatch('Cannot create resident, because another resident with the same provided set of "property", "unitName", "phone"')
                expect(data).toEqual({ 'obj': null })
            })
        })

        it('throws error on update record when another record exist with same set of fields: "property", "unitName", "phone"', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()
            const duplicatedFields = {
                property: { connect: { id: userClient.property.id } },
                unitName: faker.random.alphaNumeric(3),
                phone: createTestPhone(),
            }

            await createTestResident(adminClient, userClient.organization, userClient.property, duplicatedFields)
            const [resident] = await createTestResident(adminClient, userClient.organization, userClient.property, {
                property: { connect: { id: userClient.property.id } },
                unitName: faker.random.alphaNumeric(3),
                phone: createTestPhone(),
            })

            await catchErrorFrom(async () => {
                await updateTestResident(adminClient, resident.id, duplicatedFields)
            }, ({ errors, data }) => {
                expect(errors[0].message).toMatch('You attempted to perform an invalid mutation')
                expect(errors[0].data.messages[0]).toMatch('Cannot update resident, because another resident already exists with the same provided set of "property", "unitName", "phone"')
                expect(data).toEqual({ 'obj': null })
            })
        })

        it('throws error, when trying to connect new resident to contact, that is connected to another resident', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()
            const [contact] = await createTestContact(adminClient, userClient.organization, userClient.property)

            const contactConnection = {
                contact: { connect: { id: contact.id } },
            }

            await createTestResident(adminClient, userClient.organization, userClient.property, contactConnection)

            await catchErrorFrom(async () => {
                await createTestResident(adminClient, userClient.organization, userClient.property, contactConnection)
            }, ({ errors, data }) => {
                expect(errors[0].message).toMatch('You attempted to perform an invalid mutation')
                expect(errors[0].data.messages[0]).toMatch('Specified contact is already connected with another resident')
                expect(data).toEqual({ 'obj': null })
            })
        })

        it('throws error, when trying to connect existing resident to contact, that is connected to another resident', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()
            const [contact] = await createTestContact(adminClient, userClient.organization, userClient.property)

            const contactConnection = {
                contact: { connect: { id: contact.id } },
            }

            await createTestResident(adminClient, userClient.organization, userClient.property, contactConnection)
            const [resident] = await createTestResident(adminClient, userClient.organization, userClient.property)

            await catchErrorFrom(async () => {
                await updateTestResident(adminClient, resident.id, contactConnection)
            }, ({ errors, data }) => {
                expect(errors[0].message).toMatch('You attempted to perform an invalid mutation')
                expect(errors[0].data.messages[0]).toMatch('Specified contact is already connected with another resident')
                expect(data).toEqual({ 'obj': null })
            })
        })

        it('throws error, when trying to connect new resident to billing account, that is connected to another resident', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()

            const { context } = await makeContextWithOrganizationAndIntegrationAsAdmin()
            const [billingProperty] = await createTestBillingProperty(adminClient, context)
            const [billingAccount] = await createTestBillingAccount(adminClient, context, billingProperty)

            const billingAccountConnection = {
                billingAccount: { connect: { id: billingAccount.id } },
            }

            await createTestResident(adminClient, userClient.organization, userClient.property, billingAccountConnection)

            await catchErrorFrom(async () => {
                await createTestResident(adminClient, userClient.organization, userClient.property, billingAccountConnection)
            }, ({ errors, data }) => {
                expect(errors[0].message).toMatch('You attempted to perform an invalid mutation')
                expect(errors[0].data.messages[0]).toMatch('Specified billing account is already connected to another resident')
                expect(data).toEqual({ 'obj': null })
            })
        })

        it('throws error, when trying to connect existing resident to billing account, that is connected to another resident', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()

            const { context } = await makeContextWithOrganizationAndIntegrationAsAdmin()
            const [billingProperty] = await createTestBillingProperty(adminClient, context)
            const [billingAccount] = await createTestBillingAccount(adminClient, context, billingProperty)

            const billingAccountConnection = {
                billingAccount: { connect: { id: billingAccount.id } },
            }

            await createTestResident(adminClient, userClient.organization, userClient.property, billingAccountConnection)
            const [resident] = await createTestResident(adminClient, userClient.organization, userClient.property)

            await catchErrorFrom(async () => {
                await updateTestResident(adminClient, resident.id, billingAccountConnection)
            }, ({ errors, data }) => {
                expect(errors[0].message).toMatch('You attempted to perform an invalid mutation')
                expect(errors[0].data.messages[0]).toMatch('Specified billing account is already connected to another resident')
                expect(data).toEqual({ 'obj': null })
            })
        })

    })

    describe('normalization', async () => {
        it('converts phone to E.164 format without spaces', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()
            const fields = {
                phone: '+7 999 111-22-33',
            }
            const [obj] = await createTestResident(adminClient, userClient.organization, userClient.property, fields)
            expect(obj.phone).toEqual('+79991112233')
        })

        it('converts email to lowercase format', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()
            const fields = {
                email: 'Test@Example.com',
            }
            const [obj] = await createTestResident(adminClient, userClient.organization, userClient.property, fields)
            expect(obj.email).toEqual('test@example.com')
        })
    })

    describe('Create', () => {
        it('can be created by admin', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()

            const { context } = await makeContextWithOrganizationAndIntegrationAsAdmin()
            const [billingProperty] = await createTestBillingProperty(adminClient, context)
            const [billingAccount] = await createTestBillingAccount(adminClient, context, billingProperty)

            const [contact] = await createTestContact(adminClient, userClient.organization, userClient.property)

            const fields = {
                contact: { connect: { id: contact.id } },
                billingAccount: { connect: { id: billingAccount.id } },
            }

            const [obj, attrs] = await createTestResident(adminClient, userClient.organization, userClient.property, fields)
            expect(obj.id).toMatch(UUID_RE)
            expect(obj.dv).toEqual(1)
            expect(obj.sender).toEqual(attrs.sender)
            expect(obj.v).toEqual(1)
            expect(obj.newId).toEqual(null)
            expect(obj.deletedAt).toEqual(null)
            expect(obj.createdBy).toEqual(expect.objectContaining({ id: adminClient.user.id }))
            expect(obj.updatedBy).toEqual(expect.objectContaining({ id: adminClient.user.id }))
            expect(obj.createdAt).toMatch(DATETIME_RE)
            expect(obj.updatedAt).toMatch(DATETIME_RE)
            expect(obj.organization.id).toEqual(userClient.organization.id)
            expect(obj.property.id).toEqual(userClient.property.id)
            expect(obj.contact.id).toEqual(contact.id)
            expect(obj.billingAccount.id).toEqual(billingAccount.id)
        })

        it('can be created by user, who is employed in the same organization and has "canManageResidents" ability', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithProperty()
            const [organization] = await createTestOrganization(adminClient)
            const [role] = await createTestOrganizationEmployeeRole(adminClient, organization, {
                canManageResidents: true,
            })
            await createTestOrganizationEmployee(adminClient, organization, userClient.user, role)

            const [obj, attrs] = await createTestResident(userClient, organization, userClient.property)

            expect(obj.id).toMatch(UUID_RE)
            expect(obj.dv).toEqual(1)
            expect(obj.sender).toEqual(attrs.sender)
            expect(obj.unitName).toMatch(attrs.unitName)
            expect(obj.name).toMatch(attrs.name)
            expect(obj.phone).toMatch(attrs.phone)
            expect(obj.email).toMatch(attrs.email)
            expect(obj.organization.id).toMatch(organization.id)
            expect(obj.v).toEqual(1)
            expect(obj.newId).toEqual(null)
            expect(obj.deletedAt).toEqual(null)
            expect(obj.createdBy).toEqual(expect.objectContaining({ id: userClient.user.id }))
            expect(obj.updatedBy).toEqual(expect.objectContaining({ id: userClient.user.id }))
            expect(obj.createdAt).toMatch(DATETIME_RE)
            expect(obj.updatedAt).toMatch(DATETIME_RE)
        })

        it('cannot be created by user, who is employed in the same organization and does not have "canManageResidents" ability', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithProperty()
            const [organization] = await createTestOrganization(adminClient)
            const [anotherOrganization] = await createTestOrganization(adminClient)
            const [role] = await createTestOrganizationEmployeeRole(adminClient, organization, {
                canManageResidents: false,
            })
            await createTestOrganizationEmployee(adminClient, organization, userClient.user, role)

            await expectToThrowAccessDeniedErrorToObj(async () => {
                await createTestResident(userClient, anotherOrganization, userClient.property)
            })
        })

        it('cannot be created by user, who is not employed in specified organization', async () => {
            const user = await makeClientWithProperty()
            const anotherUser = await makeClientWithProperty()
            await expectToThrowAccessDeniedErrorToObj(async () => {
                await createTestResident(anotherUser, user.organization, user.property)
            })
        })

        it('cannot be created by anonymous', async () => {
            const userClient = await makeClientWithProperty()
            const anonymous = await makeClient()
            await expectToThrowAccessDeniedErrorToObj(async () => {
                await createTestResident(anonymous, userClient.organization, userClient.property)
            })
        })
    })

    describe('Read', async () => {
        it('can be read by admin', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()
            const [obj] = await createTestResident(adminClient, userClient.organization, userClient.property)
            const objs = await Resident.getAll(adminClient, {}, { sortBy: ['updatedAt_DESC'] })
            expect(objs.length >= 1).toBeTruthy()
            expect(objs[0].id).toMatch(obj.id)
        })

        it('can be read by user, who is employed in organization, which manages associated property', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()
            const [obj] = await createTestResident(adminClient, userClient.organization, userClient.property)
            const objs = await Resident.getAll(userClient, {}, { sortBy: ['updatedAt_DESC'] })
            expect(objs).toHaveLength(1)
            expect(objs[0].id).toMatch(obj.id)
        })

        it('cannot be read by user, who is not employed in organization, which manages associated property', async () => {
            const userClient = await makeClientWithProperty()
            const anotherUserClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()
            const [obj] = await createTestResident(adminClient, userClient.organization, userClient.property)
            await createTestResident(adminClient, anotherUserClient.organization, userClient.property)
            const objs = await Resident.getAll(userClient, {}, { sortBy: ['updatedAt_DESC'] })
            expect(objs).toHaveLength(1)
            expect(objs[0].id).toMatch(obj.id)
        })

        it('cannot be read by anonymous', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithProperty()
            const anonymousClient = await makeClient()
            await createTestResident(adminClient, userClient.organization, userClient.property)
            await expectToThrowAccessDeniedErrorToObjects(async () => {
                await Resident.getAll(anonymousClient)
            })
        })
    })

    describe('Update', () => {
        it('can be updated by admin', async () => {
            const userClient = await makeClientWithProperty()
            const adminClient = await makeLoggedInAdminClient()
            const [obj] = await createTestResident(adminClient, userClient.organization, userClient.property)

            const [objUpdated, attrs] = await updateTestResident(adminClient, obj.id)

            expect(objUpdated.id).toEqual(obj.id)
            expect(objUpdated.dv).toEqual(1)
            expect(objUpdated.sender).toEqual(attrs.sender)
            expect(objUpdated.v).toEqual(2)
            expect(objUpdated.newId).toEqual(null)
            expect(objUpdated.deletedAt).toEqual(null)
            expect(objUpdated.createdBy).toEqual(expect.objectContaining({ id: adminClient.user.id }))
            expect(objUpdated.updatedBy).toEqual(expect.objectContaining({ id: adminClient.user.id }))
            expect(objUpdated.createdAt).toMatch(DATETIME_RE)
            expect(objUpdated.updatedAt).toMatch(DATETIME_RE)
            expect(objUpdated.updatedAt).not.toEqual(objUpdated.createdAt)
            expect(objUpdated.name).toEqual(attrs.name)
            expect(objUpdated.email).toEqual(attrs.email)
            expect(objUpdated.phone).toEqual(attrs.phone)
        })

        it('can be updated by user, who is employed in the same organization and does have "canManageResidents" ability', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithProperty()
            const [organization] = await createTestOrganization(adminClient)
            const [role] = await createTestOrganizationEmployeeRole(adminClient, organization, {
                canManageResidents: true,
            })
            await createTestOrganizationEmployee(adminClient, organization, userClient.user, role)

            const [obj] = await createTestResident(adminClient, organization, userClient.property)

            const [objUpdated, attrs] = await updateTestResident(userClient, obj.id)

            expect(objUpdated.id).toEqual(obj.id)
            expect(objUpdated.dv).toEqual(1)
            expect(objUpdated.sender).toEqual(attrs.sender)
            expect(objUpdated.v).toEqual(2)
            expect(objUpdated.newId).toEqual(null)
            expect(objUpdated.deletedAt).toEqual(null)
            expect(objUpdated.createdBy).toEqual(expect.objectContaining({ id: adminClient.user.id }))
            expect(objUpdated.updatedBy).toEqual(expect.objectContaining({ id: userClient.user.id }))
            expect(objUpdated.createdAt).toMatch(DATETIME_RE)
            expect(objUpdated.updatedAt).toMatch(DATETIME_RE)
            expect(objUpdated.updatedAt).not.toEqual(objUpdated.createdAt)
            expect(objUpdated.name).toEqual(attrs.name)
            expect(objUpdated.email).toEqual(attrs.email)
            expect(objUpdated.phone).toEqual(attrs.phone)
        })

        it('cannot be updated by user, who is employed in organization and does not have "canManageResidents" ability', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithProperty()
            const [organization] = await createTestOrganization(adminClient)
            const [role] = await createTestOrganizationEmployeeRole(adminClient, organization, {
                canManageResidents: false,
            })
            await createTestOrganizationEmployee(adminClient, organization, userClient.user, role)

            const [obj] = await createTestResident(adminClient, organization, userClient.property)

            await expectToThrowAccessDeniedErrorToObj(async () => {
                await updateTestResident(userClient, obj.id)
            })
        })

        it('cannot be updated by user, who is not employed in organization, which manages associated property', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithProperty()
            const [organization] = await createTestOrganization(adminClient)
            const [role] = await createTestOrganizationEmployeeRole(adminClient, organization, {
                canManageResidents: true,
            })
            await createTestOrganizationEmployee(adminClient, organization, userClient.user, role)

            const anotherUserClient = await makeClientWithProperty()

            const [obj] = await createTestResident(adminClient, organization, userClient.property)

            await expectToThrowAccessDeniedErrorToObj(async () => {
                await updateTestResident(anotherUserClient, obj.id)
            })
        })

        it('cannot be updated by anonymous', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithProperty()
            const anonymousClient = await makeClient()

            const [obj] = await createTestResident(adminClient, userClient.organization, userClient.property)

            await expectToThrowAccessDeniedErrorToObj(async () => {
                await updateTestResident(anonymousClient, obj.id)
            })
        })
    })

    describe('Delete', () => {
        it('cannot be deleted by admin', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithProperty()

            const [obj] = await createTestResident(adminClient, userClient.organization, userClient.property)

            await expectToThrowAccessDeniedErrorToObj(async () => {
                await Resident.delete(adminClient, obj.id)
            })
        })

        it('cannot be deleted by user', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithProperty()

            const [obj] = await createTestResident(adminClient, userClient.organization, userClient.property)

            await expectToThrowAccessDeniedErrorToObj(async () => {
                await Resident.delete(userClient, obj.id)
            })
        })

        it('cannot be deleted by anonymous', async () => {
            const adminClient = await makeLoggedInAdminClient()
            const userClient = await makeClientWithProperty()
            const anonymousClient = await makeClient()

            const [obj] = await createTestResident(adminClient, userClient.organization, userClient.property)

            await expectToThrowAccessDeniedErrorToObj(async () => {
                await Resident.delete(anonymousClient, obj.id)
            })
        })
    })
})
